import React, { Component } from "react";

import {
  Button,
  Container,
  Dimmer,
  Header,
  Icon,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Table,
} from "semantic-ui-react";

import { Link } from "react-router-dom";

import {
  orderSummaryURL,
  orderItemDeleteURL,
  addToCartURL,
} from "../containers/constants";

import { authAxios } from "../Utils";

class OrderSummary extends Component {
  state = {
    data: null,
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.handleFetchOrder();
  }

  handleFetchOrder = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderSummaryURL)
      .then((res) => {
        this.setState({ data: res.data, loading: false });
      })
      .catch((err) => {
        if (err.response.status === 404) {
          this.setState({
            error: "You currently do not have an order",
            loading: false,
          });
        } else {
          this.setState({ error: err, loading: false });
        }
      });
  };

  renderVariation = (orderItem) => {
    let text = "";
    orderItem.item_variations.forEach((iv) => {
      text += `${iv.variation.name}: ${iv.value}, `;
    });
    return text;
  };

  handleFormatData = (formData) => {
    return Object.keys(formData).map((key) => {
      return formData[key];
    });
  };

  handleAddToCart = (slug, itemVariations) => {
    this.setState({ loading: true });
    const variations = this.handleFormatData(itemVariations);

    authAxios
      .post(addToCartURL, { slug, variations })
      .then((res) => {
        this.handleFetchOrder();
        this.setState({ loading: false });
      })
      .catch((err) => {
        this.setState({ error: err, loading: false });
      });
  };

  handleRemoveItem = (itemID) => {
    authAxios
      .delete(orderItemDeleteURL(itemID))
      .then((res) => {
        this.handleFetchOrder();
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  render() {
    const { data, error, loading } = this.state;
    // console.log(data);
    return (
      <Container>
        <Header as="h3">Order Summary</Header>

        {error && (
          <Message
            error
            header="There was an error"
            content={JSON.stringify(error)}
          />
        )}
        {loading && (
          <Segment>
            <Dimmer active inverted>
              <Loader inverted>Loading</Loader>
            </Dimmer>

            <Image src="https://react.semantic-ui.com/images/wireframe/short-paragraph.png" />
          </Segment>
        )}

        {data && (
          <Table celled>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>Item #</Table.HeaderCell>
                <Table.HeaderCell>Item Name</Table.HeaderCell>
                <Table.HeaderCell>Item Prince</Table.HeaderCell>
                <Table.HeaderCell>Item Quantity</Table.HeaderCell>
                <Table.HeaderCell>Total Item Price</Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
              {data.order_items.map((order_item, i) => {
                {
                  /* console.log("asdasd", order_item); */
                }
                return (
                  <Table.Row key={order_item.id}>
                    <Table.Cell>
                      <Label ribbon>{i + 1}</Label>
                    </Table.Cell>
                    <Table.Cell>
                      {order_item.item.title} -{" "}
                      {this.renderVariation(order_item)}
                    </Table.Cell>
                    <Table.Cell>${order_item.item.price}</Table.Cell>
                    <Table.Cell textAlign="center">
                      <Icon
                        name="minus"
                        style={{ float: "left", cursor: "pointer" }}
                        onClick={() => this.handleRemoveItem(order_item.id)}
                      ></Icon>
                      {order_item.quantity}
                      <Icon
                        name="plus"
                        style={{ float: "right", cursor: "pointer" }}
                        onClick={() => this.handleAddToCart(order_item.slug)}
                      ></Icon>
                    </Table.Cell>

                    <Table.Cell>
                      {order_item.item.discount_price && (
                        <Label color="green" ribbon>
                          On Discount
                        </Label>
                      )}
                      ${order_item.final_price}
                      <Icon
                        name="trash"
                        color="red"
                        style={{ float: "right", cursor: "pointer" }}
                        onClick={() => this.handleRemoveItem(order_item.id)}
                      ></Icon>
                    </Table.Cell>
                  </Table.Row>
                );
              })}
              <Table.Row>
                <Table.Cell />
                <Table.Cell />
                <Table.Cell />
                <Table.Cell textAlign="right" colSpan="2">
                  Order Total: ${data.total}
                </Table.Cell>
              </Table.Row>
            </Table.Body>

            <Table.Footer>
              <Table.Row>
                <Table.HeaderCell colSpan="5" textAlign="right">
                  <Link to="/checkout">
                    <Button color="yellow">Checkout</Button>
                  </Link>
                </Table.HeaderCell>
              </Table.Row>
            </Table.Footer>
          </Table>
        )}
      </Container>
    );
  }
}
export default OrderSummary;
