import React, { Component } from "react";

import { authAxios } from "../Utils";

import {
  Button,
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Label,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
} from "semantic-ui-react";

import {
  addressListURL,
  addressCreateURL,
  countryListURL,
  userIDURL,
} from "./constants";

const UPDATE_FORM = "UPDATE_FORM";
const CREATE_FORM = "CREATE_FORM";

class AddressForm extends React.Component {
  state = {
    formData: [{ default: false }],
    error: null,
    loading: false,
    saving: false,
    success: false,
  };
  componentDidMount() {
    const { address, formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.setState({ formData: address });
    }
  }

  handleToggleDefault = () => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      default: !formData.default,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleChange = (e) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleSelectChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [name]: value,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleSubmit = (e) => {
    this.setState({ saving: true });
    e.preventDefault();
    const { formType } = this.props;
    if (formType === UPDATE_FORM) {
      this.handleUpdateAddress();
    } else {
      this.handleCreateAddress();
    }
  };

  handleCreateAddress = (e) => {
    const { formData } = this.state;
    const { activeItem, userID } = this.props;
    authAxios
      .post(addressCreateURL, {
        ...formData,
        user: userID,
        address_type: activeItem === "billingAddress" ? "B" : "S",
      })
      .then((res) => {
        this.setState({ loading: false, success: true });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleUpdateAddress = () => {
    const { userID, activeItem } = this.props;
    const { formData } = this.state;
    authAxios
      .put(addressUpdateURL(formData.id), {
        ...formData,
        user: userID,
        address_type: activeItem === "billingAddress" ? "B" : "S",
      })
      .then((res) => {
        this.setState({
          saving: false,
          success: true,
          formData: { default: false },
        });
        this.props.callback();
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  render() {
    const { error, formData, saving, success } = this.state;
    const { countries } = this.props;
    return (
      <Form onSubmit={this.handleCreateAddress} success={success} error={error}>
        <Form.Input
          required
          name="street_address"
          placeholder="Street Address"
          onChange={this.handleChange}
        />
        <Form.Input
          required
          name="apartment_address"
          placeholder="Apartment Address"
          onChange={this.handleChange}
        />
        <Form.Field required>
          <Select
            fluid
            loading={countries.length < 1}
            options={countries}
            search
            clearble
            name="country"
            placeholder="Country"
            onChange={this.handleSelectChange}
          />
        </Form.Field>

        <Form.Input
          required
          name="zip"
          placeholder="zip"
          onChange={this.handleChange}
        />
        <Form.Checkbox
          name="default"
          label="Make this the default address?"
          onChange={this.handleToggleDefault}
          checked={formData.default}
        />
        {success && (
          <Message
            success
            header="Form completed"
            content="Your address was saved "
          />
        )}
        {error && (
          <Message
            error
            header="There was an error"
            content={JSON.stringify(error)}
          />
        )}
        <Form.Button loading={saving} disabled={saving} primary>
          Save
        </Form.Button>
      </Form>
    );
  }
}

class Profile extends Component {
  state = {
    activeItem: "billingAddress",
    addresses: [],
    countries: [],

    userID: null,
  };

  componentDidMount() {
    this.handleFetchAddresses();
    this.handleFetchCountries();
    this.handleFetchUserID();
  }

  handleItemClick = (name) => {
    this.setState({ activeItem: name }, () => {
      this.handleFetchAddresses();
    });
  };

  handleFormatCountries = (countries) => {
    const keys = Object.keys(countries);
    return keys.map((k) => {
      return { key: k, text: countries[k], value: k };
    });
  };

  handleFetchUserID = () => {
    this.setState({ loading: true });
    authAxios
      .get(userIDURL)
      .then((res) => {
        this.setState({ userID: res.data.userID });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleFetchCountries = () => {
    this.setState({ loading: true });
    authAxios
      .get(countryListURL)
      .then((res) => {
        this.setState({ countries: this.handleFormatCountries(res.data) });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    const { activeItem } = this.state;
    authAxios
      .get(addressListURL(activeItem === "billingAddress" ? "B" : "S"))
      .then((res) => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  render() {
    const {
      activeItem,
      addresses,
      countries,
      error,
      formData,
      saving,
      success,
      loading,
    } = this.state;
    return (
      <Grid container columns={2} divides>
        <Grid.Row>
          <Grid.Column columns={1}>
            {error && (
              <Message
                error
                header="There was an error"
                content={JSON.stringify(error)}
              />
            )}
            {loading && (
              <Segment>
                <Dimmer active inverted>
                  <Loader inverted>Loading</Loader>
                </Dimmer>

                <Image src="/images/wireframe/short-paragraph.png" />
              </Segment>
            )}
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={6}>
            {" "}
            <Menu pointing vertical>
              <Menu.Item
                name="Billing Address"
                active={activeItem === "billingAddress"}
                onClick={() => this.handleItemClick("billingAddress")}
              />
              <Menu.Item
                name="Shipping Address"
                active={activeItem === "shippingAddress"}
                onClick={() => this.handleItemClick("shippingAddress")}
              />
              <Menu.Item
                name="Payment History"
                active={activeItem === "paymentHistory"}
                onClick={() => this.handleItemClick("paymentHistory")}
              />
            </Menu>
          </Grid.Column>
          <Grid.Column width={10}>
            <Header>{`Update your ${
              activeItem === "billingAddress" ? "Billing" : "Shipping"
            } address`}</Header>
            <Divider />
            <Card.Group>
              {addresses.map((a) => {
                return (
                  <Card key={a.id}>
                    <Card.Content>
                      {a.default && (
                        <Label as="a" color="blue" ribbon="right">
                          Default
                        </Label>
                      )}
                      <Card.Header>
                        {a.street_address}, {a.apartment_address}
                      </Card.Header>
                      <Card.Meta>{a.country}</Card.Meta>
                      <Card.Description>{a.zip}</Card.Description>
                    </Card.Content>
                    <Card.Content extra>
                      <Button color="yellow">Update</Button>
                      <Button color="red">Delete</Button>
                    </Card.Content>
                  </Card>
                );
              })}
            </Card.Group>
            {addresses.length > 0 ? <Divider /> : null}
            <AddressForm countries={countries} formType={CREATE_FORM} />
            {/* <AddressForm
              countries={countries}
              address={selectedAddress}
              formType={UPDATE_FORM}
            /> */}
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Profile;
