import React from "react";
import { CardElement, ElementsConsumer } from "@stripe/react-stripe-js";

import { Elements } from "@stripe/react-stripe-js";
import { loadStripe } from "@stripe/stripe-js";

import {
  Button,
  Container,
  Divider,
  Dimmer,
  Header,
  Icon,
  Item,
  Image,
  Label,
  Loader,
  Message,
  Segment,
} from "semantic-ui-react";

import { checkoutURL, orderSummaryURL } from "../containers/constants";

import { authAxios } from "../Utils";

const stripePromise = loadStripe(
  "pk_test_51Ha0vNGV70HGHFAGYq6tdL9DjEXnL6uslIYoVA7TLvwLhW1EHWUWiMh0KM4PEkxkzCsy6Y0mbkjAdXCBzlPzJVbx00Mtss3kPN"
);

class OrderPreview extends React.Component {
  state = {
    data: null,
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.handleFetchOrder();
  }

  handleFetchOrder = () => {
    this.setState({ loading: true });
    authAxios
      .get(orderSummaryURL)
      .then((res) => {
        this.setState({ data: res.data, loading: false });
      })
      .catch((err) => {
        if (err.response.status === 404) {
          this.setState({
            error: "You currently do not have an active order",
            loading: false,
          });
        } else {
          this.setState({ error: err, loading: false });
        }
      });
  };

  render() {
    const { data, error, loading } = this.state;
    return (
      <React.Fragment>
        {error && (
          <Message
            error
            header="There was an error"
            content={JSON.stringify(error)}
          />
        )}
        {loading && (
          <Segment>
            <Dimmer active inverted>
              <Loader inverted>Loading</Loader>
            </Dimmer>

            <Image src="https://react.semantic-ui.com/images/wireframe/short-paragraph.png" />
          </Segment>
        )}
        {data && (
          <React.Fragment>
            <Item.Group relaxed>
              {data.order_items.map((order_item, i) => {
                console.log(order_item);

                return (
                  <Item key={i}>
                    <Item.Image size="tiny" src={``}/>

                    <Item.Content verticalAlign="middle">
                      <Item.Header as="a">
                        {order_item.quantity} x {order_item.item_obj.title}
                      </Item.Header>

                      <Item.Extra>
                        <Label>$ {order_item.final_price}</Label>
                      </Item.Extra>
                    </Item.Content>
                  </Item>
                  /* <Table.Row key={order_item.id}>
                    <Table.Cell>
                      <Label ribbon>{i}</Label>
                    </Table.Cell>
                    <Table.Cell>{order_item.item}</Table.Cell>
                    <Table.Cell>${order_item.item_obj.price}</Table.Cell>
                    <Table.Cell>{order_item.quantity}</Table.Cell>

                    <Table.Cell>
                      {order_item.item_obj.discount_price && (
                        <Label color="green" ribbon>
                          On Discount
                        </Label>
                      )}
                      ${order_item.final_price}
                    </Table.Cell>
                  </Table.Row> */
                );
              })}
            </Item.Group>

            <Item.Group>
              <Item.Content>
                <Item.Header>Order Total: ${data.total}</Item.Header>
              </Item.Content>
            </Item.Group>
          </React.Fragment>
        )}
      </React.Fragment>
    );
  }
}

class CheckoutForm extends React.Component {
  state = {
    loading: true,
    error: null,
    success: false,
  };
  handleSubmit = async (event) => {
    // Block native form submission.
    event.preventDefault();

    const { stripe, elements } = this.props;

    if (!stripe || !elements) {
      // Stripe.js has not loaded yet. Make sure to disable
      // form submission until Stripe.js has loaded.
      return;
    }

    this.setState({ loading: true });
    // Get a reference to a mounted CardElement. Elements knows how
    // to find your CardElement because there can only ever be one of
    // each type of element.
    const cardElement = elements.getElement(CardElement);

    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });
    authAxios
      .post(checkoutURL, { stripeToken: paymentMethod.card })
      .then((res) => {
        this.setState({ loading: false, success: true });
      })
      .catch((err) => {
        this.setState({ loading: false, error: err });
      });

    if (error) {
      console.log("[error]", error);
    } else {
      console.log("[PaymentMethod]", paymentMethod);
    }
  };

  render() {
    const { stripe } = this.props;
    const { error, loading, success } = this.state;
    return (
      <div>
        {error && (
          <Message negative>
            <Message.Header>Your payment was unsuccessful</Message.Header>
            <p>{JSON.stringify(error)}</p>
          </Message>
        )}
        {success && (
          <Message positive>
            <Message.Header>Your payment was successful</Message.Header>
            <p>
              Go to you profile <b>profile</b> to see the order delivery status.
            </p>
          </Message>
        )}
        <OrderPreview />
        <Divider />
        <Header>Would you like to complete the purshase ?</Header>

        <form onSubmit={this.handleSubmit}>
          <CardElement />

          <Button
            primary
            type="submit"
            disabled={!stripe}
            style={{ marginTop: "10px" }}
            // loading={loading}
            // disabled={loading}
          >
            Pay
          </Button>
        </form>
      </div>
    );
  }
}

const InjectedCheckoutForm = () => {
  return (
    <Container text>
      <h3>Complete your Order</h3>
      <Elements stripe={stripePromise}>
        <ElementsConsumer>
          {({ elements, stripe }) => (
            <CheckoutForm elements={elements} stripe={stripe} />
          )}
        </ElementsConsumer>
      </Elements>
    </Container>
  );
};

export default InjectedCheckoutForm;
