import React, { Component } from "react";
import {
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
} from "semantic-ui-react";
import { addressListURL, addressCreateURL, countryListURL } from "./constants";

import { authAxios } from "../Utils";

class Profile extends Component {
  state = {
    activeItem: "billingAddress",
    addresses: [],
    formData: [{ default: false }],
    countries: [],
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.handleFetchAddresses();
    this.handleFetchCountries();
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  handleFormatCountries = (countries) => {
    const keys = Object.keys(countries);
    return keys.map((k) => {
      return { key: k, text: countries[k], value: k };
    });
  };

  handleFetchCountries = () => {
    this.setState({ loading: true });
    authAxios
      .get(countryListURL)
      .then((res) => {
        this.setState({ countries: this.handleFormatCountries(res.data) });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL)
      .then((res) => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };
  handleToggleDefault = (e) => {
    const { formData } = this.state;
    const updatedFormData = {
      ...formData,
      default: !formData.default,
    };
    this.setState({
      formData: updatedFormData,
    });
  };

  handleSelectChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormData = {
      ...formData,
      [name]: value,
    };
    this.setState({
      formData: updatedFormData,
    });
  };

  handleCreateAddress = (e) => {
    // e.prevent.default();
    const { formData } = this.state;
    console.log(formData);
  };

  render() {
    const { activeItem, addresses, countries, error, loading } = this.state;
    return (
      <Grid container columns={2} divides>
        <Grid.Row>
          <Grid.Column columns={1}>
            {error && (
              <Message
                error
                header="There was an error"
                content={JSON.stringify(error)}
              />
            )}
            {loading && (
              <Segment>
                <Dimmer active inverted>
                  <Loader inverted>Loading</Loader>
                </Dimmer>

                <Image src="/images/wireframe/short-paragraph.png" />
              </Segment>
            )}
            {addresses.map((a) => {
              return <div>{a.street_address}</div>;
            })}
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={6}>
            {" "}
            <Menu pointing vertical>
              <Menu.Item
                name="Billing Address"
                active={activeItem === "billingAddress"}
                onClick={this.handleItemClick}
              />
              <Menu.Item
                name="Physical Address"
                active={activeItem === "physicalAddress"}
                onClick={this.handleItemClick}
              />
            </Menu>
          </Grid.Column>
          <Grid.Column width={10}>
            <Header>{`Update your ${
              activeItem === "Billing Address" ? "Billing" : "Shipping"
            } address`}</Header>
            <Divider />
            <Form onSubmit={this.handleCreateAddress}>
              <Form.Input
                required
                name="street_address"
                placeholder="Street Address"
                onChange={this.handleChange}
              />
              <Form.Input
                required
                name="apartment_address"
                placeholder="Apartment Address"
                onChange={this.handleChange}
              />
              <Form.Field required>
                <Select
                  fluid
                  loading={countries.length < 1}
                  options={countries}
                  search
                  clearble
                  name="country"
                  placeholder="Country"
                  onChange={this.handleSelectChange}
                />
              </Form.Field>

              <Form.Input
                required
                name="zip_code"
                placeholder="zip_code"
                onChange={this.handleChange}
              />
              <Form.Checkbox
                name="default"
                placeholder="Make this the default address"
                onChange={this.handleToggleDefault}
              />
              <Form.Button primary>Save</Form.Button>
            </Form>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Profile;
