import React, { Component } from "react";
import {
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Loader,
  Menu,
  Message,
  Segment,
} from "semantic-ui-react";
import { addressListURL, addressCreateURL } from "./constants";

import { authAxios } from "../Utils";

class Profile extends Component {
  state = {
    activeItem: "billingAddress",
    addresses: [],
    error: null,
    loading: false,
  };

  componentDidMount() {
    this.handleFetchAddresses();
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL)
      .then((res) => {
        console.log("asdasdad", res.data);
        this.setState({ addresses: res.data, loading: false });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleCreateAddress = (event) => {

  }

  render() {
    const { activeItem, addresses, error, loading } = this.state;

    return (
      <Grid container columns={2} divides>
        <Grid.Row>
          <Grid.Column columns={1}>
            {error && (
              <Message
                error
                header="There was an error"
                content={JSON.stringify(error)}
              />
            )}
            {loading && (
              <Segment>
                <Dimmer active inverted>
                  <Loader inverted>Loading</Loader>
                </Dimmer>

                <Image src="/images/wireframe/short-paragraph.png" />
              </Segment>
            )}
            {addresses.map((a) => {
              return <div>{a.street_address}</div>;
            })}
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={6}>
            {" "}
            <Menu pointing vertical>
              <Menu.Item
                name="Billing Address"
                active={activeItem === "billingAddress"}
                onClick={this.handleItemClick}
              />
              <Menu.Item
                name="Physical Address"
                active={activeItem === "physicalAddress"}
                onClick={this.handleItemClick}
              />
            </Menu>
          </Grid.Column>
          <Grid.Column width={10}>
            <Header>{`Update your ${
              activeItem === "Billing Address" ? "Billing" : "Shipping"
            } address`}</Header>
            <Divider />
            <Form>
              <Form.Input name="street_address" placeholder="Street Address" />
              <Form.Input
                name="apartment_address"
                placeholder="Apartment Address"
              />
              {/* Country */}
              <Form.Input name="zip_code" placeholder="zip_code" />
              <Form.Checkbox
                name="default"
                placeholder="Make this the default address"
              />
              <Form.Button primary>Save</Form.Button>
            </Form>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Profile;
