import React, { Component } from "react";

import { authAxios } from "../Utils";

import {
  Card,
  Dimmer,
  Divider,
  Form,
  Grid,
  Header,
  Image,
  Loader,
  Menu,
  Message,
  Segment,
  Select,
} from "semantic-ui-react";

import {
  addressListURL,
  addressCreateURL,
  countryListURL,
  userIDURL,
} from "./constants";

class Profile extends Component {
  state = {
    activeItem: "billingAddress",
    addresses: [],
    formData: [{ default: false }],
    countries: [],
    error: null,
    loading: false,
    saving: false,
    success: false,
    userID: null,
  };

  componentDidMount() {
    this.handleFetchAddresses();
    this.handleFetchCountries();
    this.handleFetchUserID();
  }

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  handleFormatCountries = (countries) => {
    const keys = Object.keys(countries);
    return keys.map((k) => {
      return { key: k, text: countries[k], value: k };
    });
  };

  handleFetchUserID = () => {
    this.setState({ loading: true });
    authAxios
      .get(userIDURL)
      .then((res) => {
        this.setState({ userID: res.data.userID });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleFetchCountries = () => {
    this.setState({ loading: true });
    authAxios
      .get(countryListURL)
      .then((res) => {
        this.setState({ countries: this.handleFormatCountries(res.data) });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  handleFetchAddresses = () => {
    this.setState({ loading: true });
    authAxios
      .get(addressListURL)
      .then((res) => {
        this.setState({ addresses: res.data, loading: false });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };
  handleToggleDefault = () => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      default: !formData.default,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleChange = (e) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [e.target.name]: e.target.value,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleSelectChange = (e, { name, value }) => {
    const { formData } = this.state;
    const updatedFormdata = {
      ...formData,
      [name]: value,
    };
    this.setState({
      formData: updatedFormdata,
    });
  };

  handleCreateAddress = (e) => {
    const { activeItem, formData, userID } = this.state;
    authAxios
      .post(addressCreateURL, {
        ...formData,
        user: userID,
        address_type: activeItem === "billingAddress" ? "B" : "S",
      })
      .then((res) => {
        this.setState({ loading: false, success: true });
      })
      .catch((err) => {
        this.setState({ error: err });
      });
  };

  render() {
    const {
      activeItem,
      addresses,
      countries,
      error,
      formData,
      saving,
      success,
      loading,
    } = this.state;
    return (
      <Grid container columns={2} divides>
        <Grid.Row>
          <Grid.Column columns={1}>
            {error && (
              <Message
                error
                header="There was an error"
                content={JSON.stringify(error)}
              />
            )}
            {loading && (
              <Segment>
                <Dimmer active inverted>
                  <Loader inverted>Loading</Loader>
                </Dimmer>

                <Image src="/images/wireframe/short-paragraph.png" />
              </Segment>
            )}
          
          </Grid.Column>
        </Grid.Row>
        <Grid.Row>
          <Grid.Column width={6}>
            {" "}
            <Menu pointing vertical>
              <Menu.Item
                name="Billing Address"
                active={activeItem === "billingAddress"}
                onClick={this.handleItemClick}
              />
              <Menu.Item
                name="Physical Address"
                active={activeItem === "physicalAddress"}
                onClick={this.handleItemClick}
              />
            </Menu>
          </Grid.Column>
          <Grid.Column width={10}>
            <Header>{`Update your ${
              activeItem === "Billing Address" ? "Billing" : "Shipping"
            } address`}</Header>
            <Divider />
            <Form onSubmit={this.handleCreateAddress} success={success}>
              <Form.Input
                required
                name="street_address"
                placeholder="Street Address"
                onChange={this.handleChange}
              />
              <Form.Input
                required
                name="apartment_address"
                placeholder="Apartment Address"
                onChange={this.handleChange}
              />
              <Form.Field required>
                <Select
                  fluid
                  loading={countries.length < 1}
                  options={countries}
                  search
                  clearble
                  name="country"
                  placeholder="Country"
                  onChange={this.handleSelectChange}
                />
              </Form.Field>

              <Form.Input
                required
                name="zip"
                placeholder="zip"
                onChange={this.handleChange}
              />
              <Form.Checkbox
                name="default"
                label="Make this the default address?"
                onChange={this.handleToggleDefault}
                checked={formData.default}
              />
              {success && (
                <Message
                  success
                  header="Form completed"
                  content="Your address was saved "
                />
              )}
              <Form.Button loading={saving} disabled={saving} primary>
                Save
              </Form.Button>
            </Form>
          </Grid.Column>
        </Grid.Row>
      </Grid>
    );
  }
}

export default Profile;
